bool NativeCodeBasicBlock::ReferencesAccu(int from, int to) const
{
	if (to > mIns.Size()) to = mIns.Size();
	for (int i = from; i < to; i++)
		if (mIns[i].ReferencesAccu())
			return true;
	return false;
}

