			mIns[i + 0].mMode = ASMIM_ZERO_PAGE;
			mIns[i + 0].mAddress = ireg;
			mIns[i + 0].mLive |= LIVE_MEM;
			mIns[i + 1].mAddress = breg;
			mIns[i + 3].mAddress = breg;
